import timeimport pygamefrom pygame import QUITimport pygame.freetypeimport sysimport mathimport randompygame.init()display = pygame.display.set_mode((1500, 850))clock = pygame.time.Clock()DefaultImage = pygame.image.load("pixil-frame-0 (2).png")player_walkLEFTRIGHT_images = [pygame.image.load("pixil-frame-0 (3).png"), pygame.image.load("pixil-frame-0 (4).png"),                               pygame.image.load("pixil-frame-0 (3).png"), pygame.image.load("pixil-frame-0 (4).png")]player_walkDOWN_images = [pygame.image.load("pixil-frame-0 (1).png"), pygame.image.load("pixil-frame-0 (2).png"),                          pygame.image.load("pixil-frame-0 (1).png"), pygame.image.load("pixil-frame-0 (2).png")]player_weapon = pygame.image.load("OrbofMana_Fixed.png").convert()player_weapon.set_colorkey((255, 255, 255))class Player:    def __init__(self, x, y, width, height):        self.x = x        self.y = y        self.width = width        self.height = height        self.animation_count = 0        self.moving_right = False        self.moving_left = False        self.moving_up = False        self.moving_down = False        self.current_health = 200        self.target_health = 500        self.max_health = 2000        self.health_bar_length = 400        self.health_ratio = self.max_health / self.health_bar_length        self.health_change_speed = 3        self.current_mana = 200        self.target_mana = 500        self.max_mana = 2000        self.mana_bar_length = 400        self.mana_ratio = self.max_mana / self.mana_bar_length        self.mana_change_speed = 3        self.textX_health = 165        self.textY_health = 15        self.current_mana_STRING = str(self.current_mana)        self.max_mana_STRING = str(self.max_mana)        self.textX_mana = 165        self.textY_mana = 50        self.current_mana_STRING = str(self.current_health)        self.max_mana_STRING = str(self.max_mana)    def get_damage(self, amount):        if self.target_health > 0:            self.target_health -= amount        if self.target_health < 0:            self.target_health = 0    def get_health(self, amount):        if self.target_health < self.max_health:            self.target_health += amount        if self.target_health > self.max_health:            self.target_health = self.max_health    def advanced_health(self):        transition_width = 0        transition_color = (255, 0, 0)        if self.current_health < self.target_health:            self.current_health += self.health_change_speed            transition_width = int((self.target_health - self.current_health) / self.health_ratio)            transition_color = (255, 255, 0)        if self.current_health > self.target_health:            self.current_health -= self.health_change_speed            transition_width = int((self.target_health - self.current_health) / self.health_ratio)            transition_color = (255, 255, 0)        health_bar_width = int(self.current_health / self.health_ratio)        health_bar = pygame.Rect(10, 10, health_bar_width, 25)        transition_bar = pygame.Rect(health_bar.right, 10, transition_width, 25)        pygame.draw.rect(display, (255, 0, 0), health_bar)        pygame.draw.rect(display, transition_color, transition_bar)        pygame.draw.rect(display, (255, 255, 255), (10, 10, self.health_bar_length, 25), 4)    def mana_regeneration(self):        if self.target_mana < self.max_mana:            self.target_mana += 0.1        if self.target_mana > self.max_mana:            self.target_mana = self.max_mana    def mana_usage(self, amount):        if self.target_mana > 0:            self.target_mana -= amount        if self.target_mana < 0:            self.target_mana = 0    def mana_get(self, amount):        if self.target_mana < self.max_mana:            self.target_mana += amount        if self.target_mana > self.max_mana:            self.target_mana = self.max_mana    def advanced_mana(self):        transition_width = 0        transition_color = (137, 207, 240)        if self.current_mana < self.target_mana:            self.current_mana += self.mana_change_speed            transition_width = int((self.target_mana - self.current_mana) / self.mana_ratio)            transition_color = (137, 207, 240)        if self.current_mana > self.target_mana:            self.current_mana -= self.mana_change_speed            transition_width = int((self.target_mana - self.current_mana) / self.mana_ratio)            transition_color = (137, 207, 240)        mana_bar_width = int(self.current_mana / self.mana_ratio)        mana_bar = pygame.Rect(10, 45, mana_bar_width, 25)        transition_bar = pygame.Rect(mana_bar.right, 45, transition_width, 25)        pygame.draw.rect(display, (0, 127, 255), mana_bar)        pygame.draw.rect(display, transition_color, transition_bar)        pygame.draw.rect(display, (255, 255, 255), (10, 45, self.mana_bar_length, 25), 4)    def handle_weapons(self, display):        mouse_x, mouse_y = pygame.mouse.get_pos()        rel_x, rel_y = mouse_x - player.x, mouse_y - player.y        angle = (180 / math.pi) * -math.atan2(rel_y, rel_x)        player_weapon_copy = pygame.transform.rotate(player_weapon, angle)        display.blit(player_weapon_copy, (            self.x + 15 - int(player_weapon_copy.get_width() / 2),            self.y + 25 - int(player_weapon_copy.get_height() / 2)))    def main(self, display):        if self.animation_count + 1 >= 16:            self.animation_count = 0        self.animation_count += 1        if self.moving_right:            display.blit(pygame.transform.scale(player_walkLEFTRIGHT_images[self.animation_count // 4], (32, 42)),                         (self.x, self.y))        elif self.moving_left:            display.blit(pygame.transform.scale(                pygame.transform.flip(player_walkLEFTRIGHT_images[self.animation_count // 4], True, False), (32, 42)),                (self.x, self.y))        elif self.moving_down:            display.blit(pygame.transform.scale(player_walkDOWN_images[self.animation_count // 4], (32, 42)),                         (self.x, self.y))        elif self.moving_up:            display.blit(pygame.transform.scale(player_walkDOWN_images[self.animation_count // 4], (32, 42)),                         (self.x, self.y))        else:            display.blit(pygame.transform.scale(pygame.image.load("ConstantState.png"), (32, 42)), (self.x, self.y))        self.handle_weapons(display)        self.moving_right = False        self.moving_left = False    def ShowText_Stats(self, HealthCordsX, HealthCordsY, ManaCordsX, ManaCordsY, TextSize, colorHealth, colorMana):        self.font = pygame.font.Font('alagard.ttf', TextSize)        current_health_STRING = str(self.current_health)        max_health_STRING = str(self.max_health)        stat_health = self.font.render((current_health_STRING + " / " + max_health_STRING), True, (colorHealth))        display.blit(stat_health, (HealthCordsX, HealthCordsY))        current_mana_STRING = str(self.current_mana)        max_mana_STRING = str(self.max_mana)        stat_mana = self.font.render((current_mana_STRING + " / " + max_mana_STRING), True, (colorMana))        display.blit(stat_mana, (ManaCordsX, ManaCordsY))class PlayerBullet:    def __init__(self, x, y, mouse_x, mouse_y):        self.x = x        self.y = y        self.mouse_x = mouse_x        self.mouse_y = mouse_y        self.speed = 8        self.angle = math.atan2(y - mouse_y, x - mouse_x)        self.x_vel = math.cos(self.angle) * self.speed        self.y_vel = math.sin(self.angle) * self.speed    def main(self, display):        self.x -= int(self.x_vel)        self.y -= int(self.y_vel)        pygame.draw.circle(display, (100, 100, 100), (self.x + 16, self.y + 16), 5)class Inventory:    def __init__(self, inventory_spacesX, inventory_spacesY, inventory_width, inventory_heigth):        self.inventory_spacesX = inventory_spacesX        self.inventory_spacesY = inventory_spacesY        self.inventory_width = inventory_width        self.inventory_heigth = inventory_heigth        self.PlayerTextCordsX = 165        self.PlayerTextCordsY = 15        self.GridCordsYChange = 104        self.CurrentGridCordsY = 87        self.font = pygame.font.Font('alagard.ttf', 50)        self.InventoryText = self.font.render("Player Inventory", True, (255, 255, 255))        self.TextOffsetX = 45        self.TextOffsetY = 25    def drawInventory(self):        pygame.draw.rect(display, (255, 255, 255),                         (self.inventory_spacesX, self.inventory_spacesY, self.inventory_width, self.inventory_heigth),                         3)        pygame.draw.rect(display, (255, 255, 255), (256, 81, 490, 688), 3)        pygame.draw.rect(display, (255, 255, 255), (262, self.CurrentGridCordsY, 478, 100), 2)    def clearBackground(self):        pygame.draw.rect(display, (55, 55, 55), (0, 0, 1500, 850))    # (250, 75, 1000, 700)    def gridInventory(self):        pygame.draw.rect(display, (255, 255, 255), (262, self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (503, self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (262, 2 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (503, 2 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (262, 3 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (503, 3 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (262, 4 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (503, 4 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (262, 5 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        pygame.draw.rect(display, (255, 255, 255), (503, 5 * self.GridCordsYChange + self.CurrentGridCordsY, 237, 100), 2)        display.blit(self.InventoryText, (262 + self.TextOffsetX, self.CurrentGridCordsY + self.TextOffsetY))class SlimeEnemy:    def __init__(self, x, y):        self.xOffset = None        self.x = x        self.y = y        self.animation_images = [pygame.image.load("enemy_1.png"), pygame.image.load("enemy_2.png"),                                 pygame.image.load("enemy_1.png"), pygame.image.load("enemy_2.png")]        self.animation_count = 0        self.reset_offset = 0        self.offset_x = random.randrange(-300, 300)        self.offset_y = random.randrange(-300, 300)        self.xOffsetBullet = 8        self.yOffsetBullet = 2        self.bulletX = self.x + self.xOffsetBullet        self.bulletY = self.y + self.yOffsetBullet    def main(self, display):        if self.animation_count + 5 == 16:            self.animation_count = 0        self.animation_count += 1        if self.reset_offset == 0:            self.offset_x = random.randrange(-300, 300)            self.offset_y = random.randrange(-300, 300)            self.reset_offset = random.randrange(120, 150)        else:            self.reset_offset -= 1        if player.x + self.offset_x > self.x - display_scroll[0]:            self.x += 1        elif player.x + self.offset_x < self.x - display_scroll[0]:            self.x -= 1        if player.y + self.offset_y > self.y - display_scroll[1]:            self.y += 1        elif player.y + self.offset_y < self.y - display_scroll[1]:            self.y -= 1        display.blit(pygame.transform.scale(self.animation_images[self.animation_count // 4], (32, 30)),                     (self.x - display_scroll[0], self.y - display_scroll[1]))InventoryFunction = Falseenemies = [SlimeEnemy(850, 400)]enemies_attack = []player = Player(750, 425, 32, 32)playerInventory = Inventory(250, 75, 1000, 700)display_scroll = [0, 0]player_bullets = []keysE = []while True:    display.fill((30, 30, 30))    mouse_x, mouse_y = pygame.mouse.get_pos()    for event in pygame.event.get():        if event.type == pygame.QUIT:            sys.exit()            pygame.QUIT()        if event.type == pygame.MOUSEBUTTONDOWN:            if event.button == 1:                player_bullets.append(PlayerBullet(player.x, player.y, mouse_x, mouse_y))                player.mana_usage(2)    keys = pygame.key.get_pressed()    pygame.draw.rect(display, (255, 255, 255), (100 - display_scroll[0], 100 - display_scroll[1], 16, 16))    if keys[pygame.K_UP]:        player.get_health(10)        player.mana_get(10)    if keys[pygame.K_DOWN]:        player.get_damage(10)    if keys[pygame.K_a]:        display_scroll[0] -= 5        player.moving_left = True        for bullet in player_bullets:            bullet.x += 5    else:        player.moving_left = False    if keys[pygame.K_d]:        display_scroll[0] += 5        player.moving_right = True        for bullet in player_bullets:            bullet.x -= 5    else:        player.moving_right = False    if keys[pygame.K_w]:        display_scroll[1] -= 5        player.moving_up = True        for bullet in player_bullets:            bullet.y += 5    else:        player.moving_up = False    if keys[pygame.K_s]:        display_scroll[1] += 5        player.moving_down = True        for bullet in player_bullets:            bullet.y -= 5    else:        player.moving_down = False    player.main(display)    for bullet in player_bullets:        bullet.main(display)    for bullet in player_bullets:        bullet.main(display)    for enemy in enemies:        enemy.main(display)    player.advanced_mana()    player.advanced_health()    player.mana_regeneration()    player.ShowText_Stats(165, 15, 165, 50, 20, (255, 255, 255), (255, 255, 255))    if InventoryFunction == True:        playerInventory.clearBackground()        playerInventory.drawInventory()        playerInventory.gridInventory()        player.ShowText_Stats(262, 712, 262, 742, 20, (255, 30, 30), (0, 127, 255))    if keys[pygame.K_e]:        keysE.append(1)        size = len(keysE)        if size <= 2:            keysE.remove(1)            InventoryFunction = True    if keys[pygame.K_ESCAPE]:        InventoryFunction = False    clock.tick(60)    pygame.display.update()